import React,{Component} from 'react';
import axios from 'axios';



const restdetail="http://localhost:8900/resturantlist";

class Costfilter extends Component{


    costfilter=(event)=>{
        let cost=(event.target.value).split(',');
        let mealid=parseInt(this.props.mealidnumber);
        let cityid=parseInt(this.props.cityidnumber)
        let lcost=parseInt(cost[0]);
        let hcost=parseInt(cost[1]);
        let url;
        if(event.target.value==''){
            url=`${restdetail}/${cityid}/${mealid}`
        }
        else{
            url=`${restdetail}/${cityid}/${mealid}?hcost=${hcost}&lcost=${lcost}`
        }
        axios.get(url)
       .then((response)=>{this.props.restcost(response.data)})


    }




    render(){
        return(
            <React.Fragment>
                <center>Cost</center>
                <div onChange={this.costfilter}>
                    <label className='radio'>
                        <input type='radio' value='' name='range'/>all
                    </label>
                    <label className='radio'>
                        <input type='radio' value='0,500' name='range'/>0-500
                    </label>
                    <label className='radio'>
                        <input type='radio' value='501,1000' name='range'/>500-1000
                    </label>
                    <label className='radio'>
                        <input type='radio' value='1001,1500' name='cuisine'/>1000-1500
                    </label>
                    <label className='radio'>
                            <input type='radio' value='1501,2000' name='cuisine'/>1500-2000
                    </label>
                    <label className='radio'>
                            <input type='radio' value='>2001' name='cuisine'/>2000+
                    </label>

                </div>
            </React.Fragment>
        )

    }
}

export default Costfilter;
