    setdatapercuisine(filterdata){
        this.setState({restlist:filterdata})
    }


    setdatapercost(sorteddata){
        this.setState({restlist:sorteddata})
    }

    setdatasort(sorting){
        this.setState({restlist:sorting})
    }

    render(){


        return(
            <div>


                <div className='row'>
                    <div className="col-md-2">
                        <hr/>
                        <Cuisinefilter restcuisine={(data)=>{this.setdatapercuisine(data)}}mealidnumber={this.props.match.params.id} cityidnumber={this.state.cityid}/>

                        <hr/>
                       <Costfilter restcost={(data)=>{this.setdatapercost(data)}}mealidnumber={this.props.match.params.id} cityidnumber={this.state.cityid}/>

                       <Sortfilter restsort={(data)=>{this.setdatasort(data)}} mealidpass={this.props.match.params.id} cityidpass={this.state.cityid}/>
                    </div>
                    <div className='col-md-10'>
                       <Detaildisplay restdata={this.state.restlist}/>
                    </div>
               </div>
            </div>
        )
    }


    componentDidMount(){

       // sessionStorage.setItem('mealid',this.props.match.params.id)
        var mealid=Number(this.props.match.params.id)

        axios.get(`${restdetail}/${this.state.cityid}/${mealid}`)
        .then((response)=>{this.setState({restlist:response.data})})
    }



componentDidUpdate(){}

}

export default Detail;
