import React,{Component} from 'react';
import axios from 'axios';



const restdetail="http://localhost:8900/resturantlist";


class Cuisinefilter extends Component{


    cuisinefilter=(event)=>{

       let mealid=parseInt(this.props.mealidnumber);
       let cityid=parseInt(this.props.cityidnumber)
        let cuisine=Number(event.target.value)
        let url='';

        if(cuisine==''){
        url=`${restdetail}/${cityid}/${mealid}`
        }
        else{
           url=`${restdetail}/${cityid}/${mealid}?cuisine=${cuisine}`
        }
        axios.get(url)
       .then((response)=>{this.props.restcuisine(response.data)})


    }

    render(){
        return(

            <React.Fragment>
                <center>Cuisine</center>
                <div onChange={this.cuisinefilter}>
                    <label className='radio'>
                        <input type='radio' value='' name='cuisine'/>all
                    </label>
                    <label className='radio'>
                        <input type='radio' value='1' name='cuisine'/>North indian
                    </label>
                    <label className='radio'>
                        <input type='radio' value='2' name='cuisine'/>South indian
                    </label>
                    <label className='radio'>
                        <input type='radio' value='3' name='cuisine'/>chinees
                    </label>
                    <label className='radio'>
                            <input type='radio' value='4' name='cuisine'/>Fastfood
                    </label>
                    <label className='radio'>
                            <input type='radio' value='5' name='cuisine'/>Street Food
                    </label>
                </div>
            </React.Fragment>
        )
    }

 }
  export default Cuisinefilter;
