import React,{Component} from 'react';



const orderUrl="http://localhost:8900/placeorder";
class Form extends Component{
    constructor(props){
        super(props)
        this.state={
             order_id : Math.floor(Math.random()*10000),

	         name : "",
             contact : "",
             email: "",
             address : "",
             rest_id : this.props.match.params.id,
             quantity: 0


        }
    }
    handelChangeName=(event)=>{
        this.setState({name:event.target.value})
    }
    handelChangeContact=(event)=>{
        this.setState({contact:event.target.value})
    }
    handelChangeEmail=(event)=>{
        this.setState({email:event.target.value})
    }
    handelChangeAddress=(event)=>{
        this.setState({address:event.target.value})
    }
    handelChangeQuantity=(event)=>{
        this.setState({quantity:event.target.value})
    }

    handleSubmit=()=>{
         var data={
            "order_id" : this.state.order_id,

            "name" : this.state.name,
            "contact" : this.state.contact,
            "email": this.state.email,
            "address" : this.state.address,
            "rest_id" : this.props.match.params.id,
            "quantity": this.state.quantity

         }

        fetch(orderUrl,{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },

            body:JSON.stringify(data)

        })

        .then((this.props.history.push('/Orderdisplay')))
    }

    handleCancel=()=>{
     this.props.history.push('/')
    }
    render(){
        return(
            <div className='container'>
                <div className='panel panel-success'>
                    <div className="panel-heading">
                       <h3>place order</h3> 
                    </div>
                    <div className="panel-body">
                        <div className='form-group'>
                                <label className="control-label">order_id~</label>
                                <input type='text' name='order_id'
                                value={this.state.order_id}
                                readOnly
                                className="form-control"/>
                        </div>
                        <div className='form-group'>
                                <label className="control-label">rest_id~</label>
                                <input type='text' name='rest_id'
                                value={this.state.rest_id}
                                readOnly
                                className="form-control"/>
                        </div>

                        <div className='form-group'>
                                <label className="control-label">Name~</label>
                                <input type='text' name='name'
                                value={this.state.name}
                                className="form-control"
                                onChange={this.handelChangeName}/>
                        </div>
                        <div className='form-group'>
                                <label className="control-label">Contact~</label>
                                <input type='text' name='contact'
                                value={this.state.contact}

                                className="form-control"
                                onChange={this.handelChangeContact}/>
                        </div>
                        <div className='form-group'>
                                <label className="control-label">Address~</label>
                                <input type='text' name='address'
                                value={this.state.address}

                                className="form-control"
                                onChange={this.handelChangeAddress}/>
                        </div>
                        <div className='form-group'>
                                <label className="control-label">Email~</label>
                                <input type='text' name='email'
                                value={this.state.email}

                                className="form-control"
                                onChange={this.handelChangeEmail}/>
                        </div>
                        <div className='form-group'>
                                <label className="control-label">Quantity~</label>
                                <select  name='quantity' value={this.state.quantity}className="form-control"
                                onChange={this.handelChangeQuantity}>
                                    <option value='0'>select quantity</option>
                                    <option value='1'>1</option>
                                    <option value='2'>2</option>
                                    <option value='3'>3</option>
                                    <option value='4'>4</option>
                                </select> 

                        </div>
                        <div>
                            <button className='btn btn-warning' onClick={this.handleSubmit}>placeorder</button>
                            &nbsp;
                            <button className='btn btn-primary'onClick={this.handleCancel}>cancel</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Form;
