import React from 'react';
import Search from './Search';
import Footer from './Footer';
import Quicksearch from './Quicksearch';
import './Home.css';
import Clock from './clock';







const Home=(props)=>{

  const  handleResturant=(data)=>{
        props.history.push(`/rest/${data}`)
    }



    return(
        <div>

            <div className="image">
                <div class="heading">
                    <b>Find the best resturant,cafee,bars</b>
                </div>  
                <div className="locationselect">

                        <Search restid={(data)=>{handleResturant(data)}}/>

                </div>
                <div class="logo">
                   <b>e!</b> 
                </div>
                <center>
                    <b>
                    <Clock/>
                    </b>

                </center>
            </div>
            <div>
              <Quicksearch/>
            </div>

            <hr/>
            <Footer year='2020'/>
        </div>
    )
}

export default Home;
