import React,{Component} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {Tab,Tabs,TabList,TabPanel} from 'react-tabs';
import 'react-tabs/style/react-tabs.css';


//const hotelname="http://localhost:8900/resturanthomes"

const restdetail="http://localhost:8900/resturantdetail";
class Resturant extends Component{
    constructor(props){
        super(props)

        this.state={
            rest:""
        }
    }

    backbutton=(event)=>{
        let mealid=parseInt(sessionStorage.getItem('mealid'))
        this.props.history.push(`./detail/${mealid}`)
    }
    render(){

        var {rest}=this.state
        return(
            <div className='container'>
                <div className='panel panel-warning'>
                    <div className="panel-heading">
                       <h2>{rest.name}</h2> 
                    </div>
                    <div className="panel-body">
                     <div className='row'>
                        <div className='col-md-6'>
                            <img className="img-responsive" src={rest.thumb}/>
                        </div>
                        <div className='col-md-6'>
                            <h3>{rest.name}</h3>
                            <h4>{rest.locality}</h4>
                            <h4>{rest.address}</h4>
                            <h4>{rest.cost}</h4>


                        </div>
                     </div>
                     <br/>
                     {/*copy*/}
                     <div>
                        <Tabs>
                            <TabList>
                                <Tab><span className="overview">Overview</span></Tab>
                                <Tab><span className="contact">Contact</span></Tab>
                            </TabList>

                            <TabPanel>
                                <div>
                                    <div className="about">About this Place</div>
                                    <br />
                                    <div className="cuisine">Cuisine</div>
                                    <div className="bakery"></div>
                                    <br />
                                    <div className="cuisine">Average Cost</div>
                                    <div className="bakery">{rest.cost} for two people(approx).</div>
                                </div>
                            </TabPanel>
                            <TabPanel>
                                <div>
                                    <div className="phone-number">Phone Number</div>
                                    <div className="pdigit">45456556</div>
                                    <br />
                                    <div className="bheader">{rest.name}</div>
                                    <div className="address">{rest.locality}</div>
                                </div>
                            </TabPanel>
                        </Tabs>
                        </div>
                     <div className="row">
                         <Link to='/' class="btn btn-success btn-lg" onClick={this.backbutton}>Back</Link>
                         &nbsp;
                         <Link to={`/placeorder/${this.props.match.params.id}`} class="btn btn-warning btn-lg ">place Order</Link>
                     </div>
                    </div>
                </div>

            </div>
        )
    }

    componentDidMount(){
        let restid=parseInt(this.props.match.params.id)
        //var restname=(this.props.match.id)
        //console.log(restname)
       axios.get(`${restdetail}/${restid}`)
       .then((response)=>{this.setState({rest:response.data[0]})})
     }
}

export default Resturant;
