import React, { Component } from 'react';
import axios from "axios";

const detailurl="http://localhost:8900/resturantlist";
class Sortfilter extends Component{


    sortfilter=(event)=>{
        //console.log("insortfilter")
        let cityid=parseInt(this.props.cityidpass)
        let sort=Number(event.target.value)
        //sessionStorage.setItem('mealid',this.props.match.params.id)
        let mealid=parseInt(this.props.mealidpass)
         //console.log(`${detailurl}/${cityid}/${mealid}?sort=${sort}`)
         axios.get(`${detailurl}/${cityid}/${mealid}?sort=${sort}`)

         .then((response)=>{this.props.restsort(response.data)})
       }
    render(){
        return(

                        <React.Fragment>


                          <center>sorting</center>
                            <div onChange={this.sortfilter}>
                                <label className='radio'>
                                    <input type='radio' value='-1' name='sort'/>high-low
                                </label>
                                <label className='radio'>
                                <input type='radio' value='1' name='sort'/>low-high        
                                </label>
                            </div>


                        </React.Fragment>

        )
    }
}

export default Sortfilter;
