import React,{Component} from 'react';
import axios from 'axios';
import Detaildisplay from './Detaildisplay';
import Costfilter from "./filter/Costfilter"
import Cuisinefilter from "./filter/Cuisinefilter"
import Sortfilter from "./filter/sortfilter"



const restdetail="http://localhost:8900/resturantlist";
class Detail extends Component{
    constructor(props){
        super(props)

        this.state={
            cityid:parseInt(sessionStorage.getItem("cityid"))?parseInt(sessionStorage.getItem("cityid")):1,
            restlist:[],
            cuisine:'',

        }

    }


   /* cuisinefilter=(event)=>{
    console.log("incfilter")
       //this.setState({cuisine:event.target.value})
       //console.log(event.target.value)
       let cityid=parseInt(this.state.cityid)
       let cuisine=Number(event.target.value)
       sessionStorage.setItem('mealid',this.props.match.params.id)
       let mealid=parseInt(this.props.match.params.id)
       //console.log(`${detailurl}/${this.state.cityid}/${mealid}?cuisine=${cuisine}`)
          axios.get(`${detailurl}/${cityid}/${mealid}?cuisine=${cuisine}`)

          .then((response)=>{this.setState({restarray:response.data})})
   }*/


  /* costfilter=(event)=>{
       console.log("incostfilter")
    let cityid=parseInt(this.state.cityid)
    let cost=(event.target.value).split(',')
    let lcost=Number(cost[0])
    let hcost=Number(cost[1])
    sessionStorage.setItem('mealid',this.props.match.params.id)
    let mealid=parseInt(this.props.match.params.id)
     console.log(`${detailurl}/${cityid}/${mealid}?hcost=${hcost}&lcost=${lcost}`)
     axios.get(`${detailurl}/${cityid}/${mealid}?hcost=${hcost}&lcost=${lcost}`)

     .then((response)=>{this.setState({restarray:response.data})})
   }*/



  /* sortfilter=(event)=>{
    console.log("insortfilter")
    let cityid=parseInt(this.state.cityid)
    let sort=Number(event.target.value)
    sessionStorage.setItem('mealid',this.props.match.params.id)
    let mealid=parseInt(this.props.match.params.id)
     console.log(`${detailurl}/${cityid}/${mealid}?sort=${sort}`)
     axios.get(`${detailurl}/${cityid}/${mealid}?sort=${sort}`)

     .then((response)=>{this.setState({restarray:response.data})})
   }*/






    setdatapercuisine(filterdata){
        this.setState({restlist:filterdata})
    }


    setdatapercost(sorteddata){
        this.setState({restlist:sorteddata})
    }

    setdatasort(sorting){
        this.setState({restlist:sorting})
    }

    render(){


        return(
            <div>


                <div className='row'>
                    <div className="col-md-2">
                        <hr/>
                        <Cuisinefilter restcuisine={(data)=>{this.setdatapercuisine(data)}}mealidnumber={this.props.match.params.id} cityidnumber={this.state.cityid}/>

                        <hr/>
                       <Costfilter restcost={(data)=>{this.setdatapercost(data)}}mealidnumber={this.props.match.params.id} cityidnumber={this.state.cityid}/>

                       <Sortfilter restsort={(data)=>{this.setdatasort(data)}} mealidpass={this.props.match.params.id} cityidpass={this.state.cityid}/>
                    </div>
                    <div className='col-md-10'>
                       <Detaildisplay restdata={this.state.restlist}/>
                    </div>
               </div>
            </div>
        )
    }


    componentDidMount(){

       // sessionStorage.setItem('mealid',this.props.match.params.id)
        var mealid=Number(this.props.match.params.id)

        axios.get(`${restdetail}/${this.state.cityid}/${mealid}`)
        .then((response)=>{this.setState({restlist:response.data})})
    }



componentDidUpdate(){}

}

export default Detail;
