import React, { Component } from 'react';
import './Home.css'

class Clock extends Component{
    constructor(){
        super();

        this.state={
            date:new Date()
        }
    }

    tick(){
        this.setState({date:new Date()});
    }

    componentDidMount(){
        this.timerID=setInterval(
            ()=>this.tick(),
            1000
        );
    }
    componentWillMount(){
        clearInterval(this.timerID);
    }

    render(){
        return(
            <div>
                 <li><a href="#"><span class="glyphicon glyphicon-time"></span><b class ="colo">Time~{this.state.date.toLocaleTimeString()}</b> </a></li>

            </div>
        )
    }
}

export default Clock;
